#include <stdio.h>
#include <string.h>

// Importa las bibliotecas estándar para entrada/salida y manejo de cadenas.

//
int f=0;
const int i= 10;
const int M= 10;

// Declaración de variables globales, 'f' es un contador, 'i' y 'M' son constantes.

void menu_ing();
void menu_reg();
void menu_prof();
void menu_estu();
void menu_ing_notas();
void menu_ver_notas();

// Declaración de funciones que se definirán más adelante.

//
int reg_nom_user;
int reg_pass_user;	
int pass_user ;
int nom_user ;
int ing_nota_1;
int ing_nota_2;
int ing_nota_3;

// Declaración de variables globales, que se utilizarán para almacenar información de los usuarios y las notas.

//
struct info_user{
 
	int reg_nom_user;
	int reg_pass_user;	
};

// Declaración de una estructura para almacenar la información de cada usuario (nombre y contraseña).

struct info_user I[i]={};
struct info_nota{
 
	int nota_1;
	int nota_2;
	int nota_3;
		
};

// Declaración de una estructura para almacenar las notas de cada usuario.

struct info_nota N[i]={};

// Declaración de un arreglo de estructuras para almacenar información de los usuarios y las notas.

int main (){
	
	// La función principal del programa.

	while(1){
        int opcion;
		// Bucle que se ejecuta indefinidamente hasta que se elija la opción "3".
		// Se solicita al usuario que ingrese una opción (1, 2 o 3).
		printf("BIENVENIDOS AL COLEGIO \n      CASITA AZUL\n 1.INGRESAR\n 2.REGISTRASE\n 3.EXIT\n ingrese una opcion\t");
		scanf("%d",&opcion);
		
		switch(opcion){
		
			// Se utiliza un switch para determinar qué función se debe llamar en función de la opción ingresada.
			// En caso de ingresar "1", se llama a la función "menu_ing()".
			case 1:{
					menu_ing();
					break;
				}
			// En caso de ingresar "2", se llama a la función "menu_reg()".
			case 2:{
					menu_reg();
					break;
				}
			// En caso de ingresar "3", se sale del bucle.
			case 3:{
					f=3;
				
				}
			// En caso de ingresar una opción inválida, se muestra un mensaje de error.
			default:
				printf("bi bu bi bu error vueleve a intentar");
		
		}	
	}
	
	// Mensaje final que se muestra al salir del bucle.
	printf("QUE TENGO UN BUEN DIA LES DESEA\n COLEGIO \n      CASITA AZUL ");
};

void menu_ing(){
	
	// Función para manejar el ingreso al sistema.

	printf("INGRESAR \n Usuario");
	scanf("%d",&nom_user);
	
	printf("\n contraseña");
	scanf("%d",&pass_user);
	
	// Se utiliza un bucle for para verificar las credenciales ingresadas.
	// Si se encuentran coincidencias, se llama a la función correspondiente.
	// Si no se encuentran coincidencias, se muestra un mensaje de error.
	for(int j=0;j<3;j++){
            
    	if(reg_nom_user ==nom_user && reg_pass_user==pass_user){
    		
    		menu_prof();
    	}
    	if (reg_nom_user==nom_user && reg_pass_user==pass_user){
	    	
    		menu_estu();
	    }
	    else{
		printf("NO ESTAS REGISTRADO");
	    }
	
    }
}


void menu_prof(){
     
     while(f!=3){
     
        // Se inicializa una variable 'opcion' y se imprime un menú
        // para que el profesor elija una opción.
        
        int opcion;
		printf("BIENVENIDOS AL COLEGIO \n      CASITA AZUL\n 1.INGRESAR NOMBRE ESTUDIANTES\n 2.VER NOTAS DEL ESTUDIANTE \n 3.EXIT\n ingrese una opcion\t");
		scanf("%d",&opcion);
		
		// Dependiendo de la opción seleccionada, se ejecuta una función
		// diferente. Si se ingresa la opción 1, se llama a la función
		// 'menu_ing_notas'. Si se ingresa la opción 2, se llama a la
		// función 'menu_ver_notas' y luego a 'menu_estu'. Si se ingresa
		// la opción 3, se sale del bucle.
		
		switch(opcion){
		
			case 1:{
                 
					menu_ing_notas();
					break;
				}
			case 2:{
					menu_ver_notas();
    				menu_estu();
				}
			case 3:{
					f=3;
				}
			default:
				printf("bi bu bi bu error vueleve a intentar");
		
		}	
	}    
     
}
//En resumen, esta función muestra un menú al profesor con tres opciones 
//diferentes y ejecuta una acción dependiendo de la opción seleccionada. 
//El bucle se repetirá hasta que se seleccione la opción 3, 
//momento en el que se saldrá del bucle y se terminará la función.

void menu_ing_notas(){

     // Se pide al usuario que ingrese un nombre de usuario y una
     // contraseña, y se almacenan en las variables 'nom_user' y
     // 'pass_user'.
     
     printf("INGRESAR \n Usuario");
	scanf("%d",&nom_user);
	
	printf("\n contraseña");
    scanf("%d",&pass_user);
	
    // Se realiza un bucle 'for' que se repetirá tres veces. Dentro
    // del bucle, se verifica si el nombre de usuario y la contraseña
    // ingresados coinciden con los almacenados en la estructura 'I'.
    
	for(int j=0;j<3;j++){
        if(reg_nom_user==nom_user && reg_pass_user==pass_user){
    		
            // Si las credenciales son correctas, se le pide al profesor
            // que ingrese las notas del estudiante, y se suman a las
            // notas almacenadas en la estructura 'N'.
            
    		printf("Nota 1: \n");
	        scanf("%d",&ing_nota_1);
            N[i].nota_1+=ing_nota_1;
	
    		printf("Nota 2: \n");
    		scanf("%d",&ing_nota_2);
            N[i].nota_2+=ing_nota_2;
	
    		printf("Nota 3: \n");
    		scanf("%d",&ing_nota_3);
            N[i].nota_3+=ing_nota_3;

            
	    }
	    else{
	    
            // Si las credenciales son incorrectas, se imprime un mensaje
            // de error.
            
		    printf("NO ESTAS REGISTRADO");
	    }
    } 
}
